! ----------------------------------- GET_CFACE_INDEX -------------------------------

SUBROUTINE GET_CFACE_INDEX(NM,I,J,K,XPT,YPT,ZPT,ICF)

INTEGER, INTENT(IN) :: NM,I,J,K
REAL(EB),INTENT(IN) :: XPT,YPT,ZPT
INTEGER, INTENT(OUT):: ICF

! Local Variables:
INTEGER, PARAMETER :: DELTA_IJK = 1
INTEGER :: ILO, IHI, JLO, JHI, KLO, KHI, II, JJ, KK, ICF2, JCF
REAL(EB):: DIST, DIST_CLOSE
LOGICAL :: CFACE_FOUND

ICF = 0
IF(.NOT.ALLOCATED(MESHES(NM)%CCVAR)) RETURN ! Case of NO GEOMs, return and give an error.


ILO = MAX(I-DELTA_IJK,1)
IHI = MIN(I+DELTA_IJK,MESHES(NM)%IBAR)

JLO = MAX(J-DELTA_IJK,1)
JHI = MIN(J+DELTA_IJK,MESHES(NM)%JBAR)

KLO = MAX(K-DELTA_IJK,1)
KHI = MIN(K+DELTA_IJK,MESHES(NM)%KBAR)

CFACE_FOUND = .FALSE.
DO KK=KLO,KHI
   DO JJ=JLO,JHI
      DO II=ILO,IHI
         ICF2 = MESHES(NM)%CCVAR(II,JJ,KK,IBM_IDCF)
         IF (ICF2 <= 0) CYCLE
         DO JCF=1,MESHES(NM)%CUT_FACE(ICF2)%NFACE
            DIST = SQRT( (XPT-MESHES(NM)%CUT_FACE(ICF2)%XYZCEN(IAXIS,JCF))**2._EB + &
                         (YPT-MESHES(NM)%CUT_FACE(ICF2)%XYZCEN(JAXIS,JCF))**2._EB + &
                         (ZPT-MESHES(NM)%CUT_FACE(ICF2)%XYZCEN(KAXIS,JCF))**2._EB )
            IF (.NOT.CFACE_FOUND) THEN
               ICF         = MESHES(NM)%CUT_FACE(ICF2)%CFACE_INDEX(JCF)
               DIST_CLOSE  = DIST
               CFACE_FOUND = .TRUE.
            ELSE
               IF (DIST > DIST_CLOSE) CYCLE
               ICF         = MESHES(NM)%CUT_FACE(ICF2)%CFACE_INDEX(JCF)
               DIST_CLOSE  = DIST
            ENDIF
         ENDDO
      ENDDO
   ENDDO
ENDDO
! WRITE(LU_ERR,*) 'Found device location=',I,J,K,ICF,CFACE(ICF)%X,CFACE(ICF)%Y,CFACE(ICF)%Z

RETURN
END SUBROUTINE GET_CFACE_INDEX


! ----------------------------- CCREGION_COMPUTE_VISCOSITY -------------------------

SUBROUTINE CCREGION_COMPUTE_VISCOSITY(DT,NM)

USE PHYSICAL_FUNCTIONS, ONLY: LES_FILTER_WIDTH_FUNCTION
USE TURBULENCE, ONLY: WALE_VISCOSITY
USE TURBULENCE, ONLY : WALL_MODEL

REAL(EB), INTENT(IN):: DT
INTEGER, INTENT(IN) :: NM

! Local Variables:
INTEGER :: I,J,K
REAL(EB), POINTER, DIMENSION(:,:,:) :: RHOP=>NULL(),UU=>NULL(),VV=>NULL(),WW=>NULL()
REAL(EB), POINTER, DIMENSION(:,:,:,:) :: ZZP=>NULL()
REAL(EB) :: NU_EDDY,DELTA,A_IJ(3,3),DUDX,DUDY,DUDZ,DVDX,DVDY,DVDZ,DWDX,DWDY,DWDZ

REAL(EB) :: NVEC(IAXIS:KAXIS), TVEC1(IAXIS:KAXIS), TVEC2(IAXIS:KAXIS), VEL_WALL(IAXIS:KAXIS), VEL_CELL(IAXIS:KAXIS), &
            DN, RHO_WALL, MU_WALL, SLIP_FACTOR, TT(IAXIS:KAXIS), SS(IAXIS:KAXIS), &
            U_NORM, U_ORTH, U_STRM, U_CELL, V_CELL, W_CELL, U_RELA(IAXIS:KAXIS)
INTEGER  :: ICF,IND1,IND2
TYPE(CFACE_TYPE), POINTER :: CFA
TYPE(ONE_D_M_AND_E_XFER_TYPE), POINTER :: ONE_D
TYPE(SURFACE_TYPE), POINTER :: SF

! No need to compute WALE model turbulent viscosity on cut-cell region.
IF (SIM_MODE==DNS_MODE) RETURN

IF (PREDICTOR) THEN
   RHOP => RHO
   UU   => U
   VV   => V
   WW   => W
   ZZP  => ZZ
ELSE
   RHOP => RHOS
   UU   => US
   VV   => VS
   WW   => WS
   ZZP  => ZZS
ENDIF

! Define velocities on gas cut-faces underlaying Cartesian faces.
CALL CCIBM_INTERP_FACE_VEL(DT,NM,.TRUE.)

! WALE model on cells belonging to cut-cell region:
DO K=1,KBAR
   DO J=1,JBAR
      DO I=1,IBAR
          IF(SOLID(CELL_INDEX(I,J,K))) CYCLE
          IF(CCVAR(I,J,K,IBM_CGSC)==IBM_SOLID) THEN
             MU(I,J,K) = MU_DNS(I,J,K)
             CYCLE
          ENDIF
          IF(.NOT.(CCVAR(I,J,K,IBM_CGSC)==IBM_CUTCFE .OR. CCVAR(I,J,K,IBM_UNKZ)>0)) CYCLE

          DELTA = LES_FILTER_WIDTH_FUNCTION(DX(I),DY(J),DZ(K))
          ! compute velocity gradient tensor
          DUDX = RDX(I)*(UU(I,J,K)-UU(I-1,J,K))
          DVDY = RDY(J)*(VV(I,J,K)-VV(I,J-1,K))
          DWDZ = RDZ(K)*(WW(I,J,K)-WW(I,J,K-1))
          DUDY = 0.25_EB*RDY(J)*(UU(I,J+1,K)-UU(I,J-1,K)+UU(I-1,J+1,K)-UU(I-1,J-1,K))
          DUDZ = 0.25_EB*RDZ(K)*(UU(I,J,K+1)-UU(I,J,K-1)+UU(I-1,J,K+1)-UU(I-1,J,K-1))
          DVDX = 0.25_EB*RDX(I)*(VV(I+1,J,K)-VV(I-1,J,K)+VV(I+1,J-1,K)-VV(I-1,J-1,K))
          DVDZ = 0.25_EB*RDZ(K)*(VV(I,J,K+1)-VV(I,J,K-1)+VV(I,J-1,K+1)-VV(I,J-1,K-1))
          DWDX = 0.25_EB*RDX(I)*(WW(I+1,J,K)-WW(I-1,J,K)+WW(I+1,J,K-1)-WW(I-1,J,K-1))
          DWDY = 0.25_EB*RDY(J)*(WW(I,J+1,K)-WW(I,J-1,K)+WW(I,J+1,K-1)-WW(I,J-1,K-1))
          A_IJ(1,1)=DUDX; A_IJ(1,2)=DUDY; A_IJ(1,3)=DUDZ
          A_IJ(2,1)=DVDX; A_IJ(2,2)=DVDY; A_IJ(2,3)=DVDZ
          A_IJ(3,1)=DWDX; A_IJ(3,2)=DWDY; A_IJ(3,3)=DWDZ

          CALL WALE_VISCOSITY(NU_EDDY,A_IJ,DELTA)

          MU(I,J,K) = MU_DNS(I,J,K) + RHOP(I,J,K)*NU_EDDY

      ENDDO
   ENDDO
ENDDO

CALL CCIBM_INTERP_FACE_VEL(DT,NM,.FALSE.)


! Now compute U_TAU and Y_PLUS on CFACES:
DO ICF=1,N_CFACE_CELLS

   CFA => CFACE(ICF)
   ONE_D => CFA%ONE_D
   SF => SURFACE(CFA%SURF_INDEX)

   ! Surface Velocity:
   NVEC = CFA%NVEC

   ! right now VEL_T not defined for CFACEs
   TVEC1=(/ 0._EB,0._EB,0._EB/)
   TVEC2=(/ 0._EB,0._EB,0._EB/)
   ! velocity vector of the surface
   VEL_WALL = -ONE_D%UW*NVEC + SF%VEL_T(1)*TVEC1 + SF%VEL_T(2)*TVEC2

   ! find cut-cell adjacent to CFACE
   IND1 = CFA%CUT_FACE_IND1
   IND2 = CFA%CUT_FACE_IND2
   CALL GET_UVWGAS_CFACE(U_CELL,V_CELL,W_CELL,IND1,IND2)
   ! velocity vector in the centroid of the gas (cut) cell
   VEL_CELL = (/U_CELL,V_CELL,W_CELL/) ! (/1._EB,0._EB,0._EB/) ! test

   CALL GET_MUDNS_CFACE(MU_WALL,IND1,IND2)

   ! Gives local velocity components U_STRM , U_ORTH , U_NORM
   ! in terms of unit vectors SS,TT,NN:
   U_RELA(IAXIS:KAXIS) = VEL_CELL(IAXIS:KAXIS)-VEL_WALL(IAXIS:KAXIS)
   CALL GET_LOCAL_VELOCITY(U_RELA,NVEC,TT,SS,U_NORM,U_ORTH,U_STRM)

   DN  = 1._EB/ONE_D%RDN
   RHO_WALL = ONE_D%RHO_F

   CALL WALL_MODEL(SLIP_FACTOR,ONE_D%U_TAU,ONE_D%Y_PLUS,U_STRM,MU_WALL/RHO_WALL,DN,SF%ROUGHNESS)

ENDDO

RETURN
END SUBROUTINE CCREGION_COMPUTE_VISCOSITY


! --------------------------------- RANDOM_CFACE_XYZ -------------------------------

SUBROUTINE RANDOM_CFACE_XYZ(CFA,CFA_X,CFA_Y,CFA_Z)

TYPE(CFACE_TYPE), INTENT(IN) :: CFA
REAL(EB), INTENT(OUT) :: CFA_X,CFA_Y,CFA_Z

! Local Variables:
INTEGER :: IND1, IND2, ITRI, N_TRI, INOD_2, INOD_3
REAL(EB):: RN, RN_I, E1, E2, E3, V12(IAXIS:KAXIS), V13(IAXIS:KAXIS)

IND1 = CFA%CUT_FACE_IND1
IND2 = CFA%CUT_FACE_IND2

! Number of triangles that will cover the boundary cut-face convex polygon is same as NVERT for the cut-face:
N_TRI= CUT_FACE(IND1)%CFELEM(1,IND2)

! First pick randomly one triangle weighting by area:
CALL RANDOM_NUMBER(RN)
RN_I  = 0._EB
CFTRI_LOOP : DO ITRI=1,N_TRI
   ! Compute triangle Area:
   ! INOD_1 is polygon centroid, CFA%X, CFA%Y, CFA%Z
   ! VERTEX locations:
   ! Vertex 2 and 3 of triangle in local CFELEM indexing
   INOD_2 = ITRI
   INOD_3 = 1; IF (ITRI /= N_TRI) INOD_3 = ITRI+1
   ! Vertex 2 and 3 of traingle in XYZVERT indexing
   INOD_2 = CUT_FACE(IND1)%CFELEM(1+INOD_2,IND2)
   INOD_3 = CUT_FACE(IND1)%CFELEM(1+INOD_3,IND2)

   ! Compute triangles Area / AreaTOT for CFACE polygon:
   V12(IAXIS:KAXIS) = (/ CUT_FACE(IND1)%XYZVERT(IAXIS,INOD_2)-CFA%X, &
                         CUT_FACE(IND1)%XYZVERT(JAXIS,INOD_2)-CFA%Y, &
                         CUT_FACE(IND1)%XYZVERT(KAXIS,INOD_2)-CFA%Z /)
   V13(IAXIS:KAXIS) = (/ CUT_FACE(IND1)%XYZVERT(IAXIS,INOD_3)-CFA%X, &
                         CUT_FACE(IND1)%XYZVERT(JAXIS,INOD_3)-CFA%Y, &
                         CUT_FACE(IND1)%XYZVERT(KAXIS,INOD_3)-CFA%Z /)

   RN_I = RN_I + 0.5_EB/CFA%AREA * SQRT( (V12(JAXIS)*V13(KAXIS)-V12(KAXIS)*V13(JAXIS))**2 + &
                                         (V12(KAXIS)*V13(IAXIS)-V12(IAXIS)*V13(KAXIS))**2 + &
                                         (V12(IAXIS)*V13(JAXIS)-V12(JAXIS)*V13(IAXIS))**2 )
   IF (RN_I > RN) EXIT CFTRI_LOOP
ENDDO CFTRI_LOOP

! Randomly define natural coordinates for the triangle:
CALL RANDOM_NUMBER(E2)
CALL RANDOM_NUMBER(E3)
E3 = (1._EB-E2)*E3
E1 = 1._EB-E2-E3

! Compute physical coordinates of point:
CFA_X = E1*CFA%X + E2*CUT_FACE(IND1)%XYZVERT(IAXIS,INOD_2) + E3*CUT_FACE(IND1)%XYZVERT(IAXIS,INOD_3)
CFA_Y = E1*CFA%Y + E2*CUT_FACE(IND1)%XYZVERT(JAXIS,INOD_2) + E3*CUT_FACE(IND1)%XYZVERT(JAXIS,INOD_3)
CFA_Z = E1*CFA%Z + E2*CUT_FACE(IND1)%XYZVERT(KAXIS,INOD_2) + E3*CUT_FACE(IND1)%XYZVERT(KAXIS,INOD_3)

RETURN
END SUBROUTINE RANDOM_CFACE_XYZ


! -------------------------------- ADD_Q_DOT_CUTCELLS ------------------------------

SUBROUTINE ADD_Q_DOT_CUTCELLS(NM,QCOMB,QRAD,QPRES,SP_ENTH)

! This routine assumes POINT_TO_MESH(NM) has already been called for mesh NM.

USE PHYSICAL_FUNCTIONS, ONLY : GET_SENSIBLE_ENTHALPY

INTEGER, INTENT(IN) :: NM
REAL(EB), INTENT(INOUT) :: QCOMB,QRAD,QPRES,SP_ENTH

! Local Variables:
INTEGER :: ICC, JCC, I, J, K
REAL(EB):: VC,ZZ_GET(1:N_TRACKED_SPECIES),H_S

DO ICC=1,MESHES(NM)%N_CUTCELL_MESH
   I = CUT_CELL(ICC)%IJK(IAXIS)
   J = CUT_CELL(ICC)%IJK(JAXIS)
   K = CUT_CELL(ICC)%IJK(KAXIS)
   VC = DX(I)*DY(J)*DZ(K)
   QPRES = QPRES + 0.5_EB*(D_PBAR_DT_S(PRESSURE_ZONE(I,J,K))+D_PBAR_DT(PRESSURE_ZONE(I,J,K)))*VC
   DO JCC=1,CUT_CELL(ICC)%NCELL
    QCOMB = QCOMB + CUT_CELL(ICC)%Q(JCC)*CUT_CELL(ICC)%VOLUME(JCC)
    QRAD  = QRAD  + CUT_CELL(ICC)%QR(JCC)*CUT_CELL(ICC)%VOLUME(JCC)
    ZZ_GET(1:N_TRACKED_SPECIES) = CUT_CELL(ICC)%ZZ(1:N_TRACKED_SPECIES,JCC)
    CALL GET_SENSIBLE_ENTHALPY(ZZ_GET,H_S,CUT_CELL(ICC)%TMP(JCC))
    SP_ENTH = SP_ENTH + CUT_CELL(ICC)%RHO(JCC)*H_S*CUT_CELL(ICC)%VOLUME(JCC)
   ENDDO
ENDDO


RETURN
END SUBROUTINE ADD_Q_DOT_CUTCELLS



! -------------------------- CFACE_PREDICT_NORMAL_VELOCITY -------------------------

SUBROUTINE CFACE_PREDICT_NORMAL_VELOCITY(T,DT)


USE MATH_FUNCTIONS, ONLY : EVALUATE_RAMP

REAL(EB), INTENT(IN) :: T, DT

! Local variables:
INTEGER :: ICF, KK
TYPE(CFACE_TYPE), POINTER :: CFA=>NULL()
TYPE(SURFACE_TYPE), POINTER :: SF
REAL(EB), POINTER, DIMENSION(:,:) :: PBAR_P
REAL(EB) :: DELTA_P, TSI, TIME_RAMP_FACTOR, PRES_RAMP_FACTOR, VEL_INTO_BOD0

SELECT CASE(PREDICTOR)
   CASE(.TRUE.)
      PBAR_P => PBAR_S
   CASE(.FALSE.)
      PBAR_P => PBAR
END SELECT

PREDICT_NORMALS: IF (PREDICTOR) THEN

   CFACE_LOOP: DO ICF=1,N_CFACE_CELLS

      CFA => CFACE(ICF)

      WALL_CELL_TYPE: SELECT CASE (CFA%BOUNDARY_TYPE)

         CASE (NULL_BOUNDARY)

            CFA%ONE_D%UWS = 0._EB

         CASE (SOLID_BOUNDARY)

            SF => SURFACE(CFA%SURF_INDEX)

            IF (SF%SPECIES_BC_INDEX==SPECIFIED_MASS_FLUX .OR. &
                ANY(SF%LEAK_PATH>0))                          &
                CYCLE CFACE_LOOP

            IF (ABS(CFA%ONE_D%T_IGN-T_BEGIN) < SPACING(CFA%ONE_D%T_IGN) .AND. SF%RAMP_INDEX(TIME_VELO)>=1) THEN
               TSI = T + DT
            ELSE
               TSI = T + DT - CFA%ONE_D%T_IGN
               IF (TSI<0._EB) THEN
                  CFA%ONE_D%UWS = 0._EB
                  CYCLE CFACE_LOOP
               ENDIF
            ENDIF
            TIME_RAMP_FACTOR = EVALUATE_RAMP(TSI,SF%TAU(TIME_VELO),SF%RAMP_INDEX(TIME_VELO))
            KK               = CFA%ONE_D%KK
            DELTA_P          = PBAR_P(KK,SF%DUCT_PATH(1)) - PBAR_P(KK,SF%DUCT_PATH(2))
            PRES_RAMP_FACTOR = SIGN(1._EB,SF%MAX_PRESSURE-DELTA_P)*SQRT(ABS((DELTA_P-SF%MAX_PRESSURE)/SF%MAX_PRESSURE))

            VEL_INTO_BOD0    =-(CFA%NVEC(IAXIS)*U0 + CFA%NVEC(JAXIS)*V0 + CFA%NVEC(KAXIS)*W0)

            CFA%ONE_D%UWS    = VEL_INTO_BOD0 + TIME_RAMP_FACTOR*(CFA%UW0-VEL_INTO_BOD0)

            ! Special Cases
            ! NEUMANN_IF: IF (SF%SPECIFIED_NORMAL_GRADIENT) THEN
            ! TO DO, following PREDICT_NORMAL_VELOCITY.

            IF (ABS(SURFACE(CFA%SURF_INDEX)%MASS_FLUX_TOTAL)>=TWO_EPSILON_EB) CFA%ONE_D%UWS = CFA%ONE_D%UWS*RHOA/CFA%ONE_D%RHO_F

            ! VENT_IF: IF (WC%VENT_INDEX>0) THEN
            ! TO DO, following PREDICT_NORMAL_VELOCITY.

      END SELECT WALL_CELL_TYPE

   ENDDO CFACE_LOOP

ELSE PREDICT_NORMALS

   ! In the CORRECTOR step, the normal component of velocity, UW, is the same as the predicted value, UWS.
   ! However, for species mass fluxes and HVAC, UW is computed elsewhere (wall.f90).

   CFACE_LOOPC: DO ICF=1,N_CFACE_CELLS
      CFA => CFACE(ICF)
      IF (CFA%BOUNDARY_TYPE==SOLID_BOUNDARY) THEN
         SF => SURFACE(CFA%SURF_INDEX)
         IF (SF%SPECIES_BC_INDEX==SPECIFIED_MASS_FLUX .OR. &
             ANY(SF%LEAK_PATH>0)) CYCLE
      ENDIF
      CFA%ONE_D%UW = CFA%ONE_D%UWS
   ENDDO CFACE_LOOPC

ENDIF PREDICT_NORMALS

RETURN

END SUBROUTINE CFACE_PREDICT_NORMAL_VELOCITY


