      IBM_RCFACE_Z(IFACE)%JD(1:2,1:2) = 0
      DO LOCROW=LOCROW_1,LOCROW_2
         DO IIND=LOW_IND,HIGH_IND
            NII = NNZ_D_MAT_Z(IND_LOC(LOCROW))
