      CALL MOVE_ALLOC(FROM=MESHES(NM)%CUT_CELL(ICC1)%INTCOEF_CCCEN    ,TO=CUT_CELL_AUX(ICC1)%INTCOEF_CCCEN)

      CALL MOVE_ALLOC(FROM=MESHES(NM)%CUT_CELL(ICC1)%H_CCCEN          ,TO=CUT_CELL_AUX(ICC1)%H_CCCEN)
