! REAL(FB) FUNCTION PT_LINE_DISTANCE_2D(X0,Y0,X1,Y1,X2,Y2)
!
! REAL(FB), INTENT(IN) :: X0,Y0,X1,Y1,X2,Y2
!
! ! Local Variables:
! REAL(FB), PARAMETER :: EPS_FB = 1.E-7_FB
! REAL(FB) :: X3,Y3
! REAL(FB) :: DX01,DY01,DX21,DY21,DSQ
! REAL(FB) :: DPRM = -1._FB
