! DX01 = X0 - X1
! DY01 = Y0 - Y1
! DX21 = X2 - X1
! DY21 = Y2 - Y1
! DSQ  = DX21**2._FB + DY21**2._FB
! IF (DSQ > EPS_FB**2) DPRM=(DX01*DX21+DY01*DY21)/DSQ
! IF (DPRM < EPS_FB) THEN
!    X3=X1; Y3=Y1
! ELSE IF (DPRM > (1._FB+EPS_FB)) THEN
!    X3=X2; Y3=Y2
! ELSE
!    X3 = X1 + DPRM*DX21
!    Y3 = Y1 + DPRM*DY21
