!          P1(1)=BB(1)
!          P1(2)=BB(4)
!          P1(3)=BB(6)
!    END SELECT
!    CALL LINE_SEG_TRI_PLANE_INTERSECT(IERR,IERR2,Q,V1,V2,V3,P0,P1)
!
!    IF (IERR==1) THEN
!       NP=NP+1
